sourcesContent:
const { MongoClient } = require('mongodb');

class DBClient {
  constructor() {
    const host = process.env.DB_HOST || 'localhost';
    const port = process.env.DB_PORT || 27017;
    const database = process.env.DB_DATABASE || 'files_manager';

    const url = `mongodb://${host}:${port}`;

    // Initialize the MongoClient but do not connect yet
    this.client = new MongoClient(url, { useUnifiedTopology: true });

    // Create a promise that resolves when the connection is established
    this.connecting = this.client.connect().then(() => {
      console.log('Connected to MongoDB');
      this.db = this.client.db(database); // Assign the database after connection
    }).catch((err) => {
      console.error('Failed to connect to MongoDB:', err);
    });
  }

  async isAlive() {
    await this.connecting; // Ensure the connection is established
    return this.client.topology && this.client.topology.isConnected(); // New way to check connection
  }

  async nbUsers() {
    await this.connecting; // Ensure connection is established
    if (!this.db) {
      console.error('Database connection not initialized.');
      return 0;
    }
    try {
      return await this.db.collection('users').countDocuments();
    } catch (error) {
      console.error('Error counting users:', error);
      return 0;
    }
  }

  async nbFiles() {
    await this.connecting; // Ensure connection is established
    if (!this.db) {
      console.error('Database connection not initialized.');
      return 0;
    }
    try {
      return await this.db.collection('files').countDocuments();
    } catch (error) {
      console.error('Error counting files:', error);
      return 0;
    }
  }
}

const dbClient = new DBClient();
module.exports = dbClient;

